package main

import (
	"fmt"
)

/*给定一个只包括 '('，')'，'{'，'}'，'['，']' 的字符串，判断字符串是否有效。
有效字符串需满足：
1、左括号必须用相同类型的右括号闭合。
2、左括号必须以正确的顺序闭合。
3、注意空字符串可被认为是有效字符串。   *********特别注意 空字符

示例1:  输入: "()"      输出: true  ;
示例2:  输入: "()[]{}"  输出: true  ;
示例3:  输入: "(]"      输出: false ;
示例4:  输入: "([)]"    输出: false ;
示例5:  输入: "{[]}"    输出: true  ;

*/

//利用栈的思想，将遇到的左括号放进 []string 中，以后遇到右括号，从[]string 中的尾部取，与 () [] {} 比较。
func isValid(s string) bool {
	var str []string
	l := len(s)
	if l % 2 != 0 {
		return false
	}
	for i := 0;i < l;i++ {
		if s[i:i+1] == "(" || s[i:i+1] == "[" || s[i:i+1] == "{" {
			str = append(str,s[i:i+1])
			if len(str) > l / 2 {
				return false
			}
		}else {
			if len(str) == 0 {
				return false
			}
			s := fmt.Sprint(str[len(str)- 1],s[i:i+1])
			if s == "()" || s == "{}" || s == "[]" {
				lstr := len(str)
				str = str[0:lstr-1]
			}else {
				return false
			}
		}
	}
	if len(str) != 0 {
		return false
	}
	return true
}


//利用栈 先进先出的思想  （写栈太麻烦，可以用一个整数实现栈的思想） 当整数的位数大于 s 长度的 一半时  可以返回 false 了
// *************************  注意，此方法输入的长度太长会溢出
func isValid2(s string) bool {
	num := 0
	k := 0
	l := len(s)
	if l % 2 != 0 {
		return false
	}
	for i := 0;i < l; i++ {
		if sw(s[i:i+1]) > 0 {
			num = num * 10 + sw(s[i:i+1])
			k++   //记录此时num 的位数
			if k > l / 2 {
				return false
			}
		}else {
			if num % 10 + sw(s[i:i+1]) == 0 {
				num /= 10
				k--
			}else {
				return false
			}
		}
		fmt.Println(num)
	}
	if k != 0 {
		return false
	}
	return true
}

func sw(s string) int {
	flag := 0
	switch s {
	case "(" :
		flag = 1
	case "[":
		flag = 2
	case "{":
		flag = 3
	case "}":
		flag = -3
	case "]":
		flag = -2
	case ")":
		flag = -1
	default:
		flag = 0
	}
	return flag
}


